import express from "express";
import cors from "cors";
import dotenv from "dotenv";

dotenv.config();

const app = express();

// ðŸ” CORS: pon exactamente el origen del frontend
const FRONTEND_ORIGIN = process.env.FRONTEND_ORIGIN || "http://localhost:3000";
app.use(cors({ origin: FRONTEND_ORIGIN, credentials: true }));

app.use(express.json());

// âœ… Ruta de salud para probar rÃ¡pido
app.get("/api/health", (req, res) => res.json({ ok: true }));

// Ejemplo de rutas reales (ajusta a tus controladores)
import materiasRouter from "./routes/materias.routes.js";
import clasifRouter from "./routes/clasificacion.routes.js";
app.use("/api/materias", materiasRouter);
app.use("/api/clasificaciones", clasifRouter);

// Arranque
const PORT = process.env.PORT || 4000;
app.listen(PORT, () => {
  console.log(`API escuchando en http://localhost:${PORT}`);
});
